/* eslint-disable-next-line @typescript-eslint/no-require-imports */
const fs = require("fs");
const options = { encoding: "utf-8" };
const filePath = process.argv[2];
const versionStamp = process.argv[3];
const content = JSON.parse(fs.readFileSync(filePath, options));
content.version = versionStamp;
fs.writeFileSync(filePath, JSON.stringify(content, undefined, 4), options);
